import React, { Component, useState } from 'react';
import { Link } from 'react-router-dom';
// import { Dropdown,Menu,Item, Toggle,DropdownButton,ItemText } from 'react-bootstrap';
import {Parser} from 'html-react-parser';
class Productpage extends Component {

    constructor(props){
        super(props);
        this.categoryFild = this.categoryFild.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
        this.state = {
            data:"",
            dataList: [],

        }
        };

    categoryFild(e){
        this.setState({
            data: [e.target.value]
        })
    }

    onSubmit(e){
        e.preventDefault()
        let dataList=this.state.dataList
        dataList.push(this.state.data)

        this.setState({
            data:'',
            dataList
        })
        console.log(this.state)
    }

    componentDidMount(){
        let userData;
        this.userData = JSON.parse(localStorage.getItem('user'));

        if(localStorage.getItem('user')){
            this.setState({
                dataList: this.state.dataList,
            })
        }
        else{
            this.setState({
                dataList:'',
            })
        }
    }

    componentWillUpdate(nextProps, nextState){
       localStorage.setItem('user',JSON.stringify(nextState));
    }
   renderOption () {

     return  this.state.dataList.map((x) => (
        <li>

                {x}

        </li>
       ))

    }

    render() {



        // categoryFild =(e) =>  {



        // };                                                                       

        // addCategory = () => {

        // };
        return (
            <div className="home-con">
                <div className="add-box">
                    <label>Input Product</label>
                    <br/>

                    <input onChange={this.categoryFild} value={this.state.data} type="text"/>
                    <br/>
                    <button onClick={this.onSubmit}> ADD</button>
                    <br/>
                </div>
                <div className="product-box">
                      {this.renderOption()} 
                </div>
            </div>
        );
    }
}

export default Productpage;
